import 'package:flutter/material.dart';

class SampleImage extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    final size = MediaQuery.of(context).size;
    return Scaffold(
      body: Container(
        width: size.width,
        child: Column(
          mainAxisAlignment: MainAxisAlignment.center,
          children: [
            Text(
              "Boralugoda",
              style: TextStyle(fontSize: 24, fontWeight: FontWeight.w400),
            ),
            SizedBox(
              height: 20.0,
            ),
            Container(
              width: 200,
              height: 200,
              color: Colors.green,
              child: Image.asset(
                "images/photo_1.jpg",
                fit: BoxFit.cover,
              ),
            ),
            SizedBox(
              height: 40.0,
            ),
            Text(
              "Mickey Mouse",
              style: TextStyle(fontSize: 24, fontWeight: FontWeight.w400),
            ),
            SizedBox(
              height: 20.0,
            ),
            Container(
              width: 200,
              height: 200,
              color: Colors.green,
              child: Column(
                children: [
                  Image.network(
                      "data:image/jpeg;base64,"),
                  CircularProgressIndicator(),
                ],
              ),
            ),
          ],
        ),
      ),
